        destroy_tuple(TTemp[SP-1]);
        TTemp[SP-1]=TTemp[SP];
        create_tuple_i(&TTemp[SP++],0);
        T_tuple_greater_equal_elem(hv_ClassIDsNoOrientation,TTemp[SP-1],&TTemp[SP]);
        destroy_tuple(TTemp[SP-1]);
        TTemp[SP-1]=TTemp[SP];
        T_tuple_sum(TTemp[SP-1],&TTemp[SP]);
        destroy_tuple(TTemp[SP-1]);
        TTemp[SP-1]=TTemp[SP];
