        T_tuple_not_equal(TTemp[SP-2],TTemp[SP-1],&TTemp[SP]);
        destroy_tuple(TTemp[SP-2]);
        destroy_tuple(TTemp[SP-1]);
        TTemp[SP-2]=TTemp[SP];
        SP--;
        T_tuple_and(TTemp[SP-2],TTemp[SP-1],&TTemp[SP]);
        destroy_tuple(TTemp[SP-2]);
        destroy_tuple(TTemp[SP-1]);
        TTemp[SP-2]=TTemp[SP];
