   if(get_i(TTemp[SP],0)) break;
   destroy_tuple(TTemp[SP]);
   /*========== for ==========*/

    /*OcrResult := OcrResults[Index]*/
    T_tuple_select(hv_OcrResults,hv_Index,&TTemp[SP++]);
    destroy_tuple(hv_OcrResult);
    hv_OcrResult=TTemp[--SP];

    /*Check if input is valid*/
