          /*flush_buffer (WindowHandles[WindowIndex])*/
          T_tuple_select(hv_WindowHandles,hv_WindowIndex,&TTemp[SP++]);
          /***/T_flush_buffer(TTemp[SP-1]);
          destroy_tuple(TTemp[--SP]);

