        /*dev_disp_text (BboxLabels, 'image', LabelRow, LabelCol, TextColorClasses, ['box_color', 'shadow', 'border_radius'], [BboxLabelColor,'false', 0])*/
        create_tuple_s(&TTemp[SP++],"image");
        create_tuple(&TTemp[SP++],3);
