  destroy_tuple(hv_InitImage);
  /***/T_get_system(TTemp[SP-1], &hv_InitImage);
  destroy_tuple(TTemp[--SP]);

  /*========== if (InitImage == 'false') ==========*/
  create_tuple_s(&TTemp[SP++],"false");
  T_tuple_equal(hv_InitImage,TTemp[SP-1],&TTemp[SP]);
  destroy_tuple(TTemp[SP-1]);
  TTemp[SP-1]=TTemp[SP];
