  destroy_tuple(TTemp[SP-2]);
  destroy_tuple(TTemp[SP-1]);
  TTemp[SP-2]=TTemp[SP];
  SP--;
  destroy_tuple(hv_DiameterC);
  hv_DiameterC=TTemp[--SP];

  /*Loop over word boxes.*/
  /*========== for IndexW := 0 to |__get_dict_tuple( DLSampleTargets, ['bbox_label_id'])| - 1 by 1 ==========*/
