      /***/T_set_dict_tuple(hv_DLPreprocessParamHighRes, TTemp[SP-1], hv_InputImageWidth);
      destroy_tuple(TTemp[--SP]);

      /*set_dict_tuple (DLPreprocessParamHighRes, ['image_height'], InputImageHeight)*/
